 Guage Den nuværende bør skiftes ud med http://bernii.github.io/gauge.js/ som er meget lettere
Countdown &lt;span id=&quot;days&quot;&gt;&lt;/span&gt; &lt;span id=&quot;hours&quot;&gt;&lt;/span&gt; &lt;span id=&quot;minutes&quot;&gt;&lt;/span&gt; &lt;span id=&quot;seconds&quot;&gt;&lt;/span&gt;  &lt;script type=&quot;text/javascript&quot;&gt; function countdown(endDate) { let days, hours, minutes, seconds; endDate = new Date(endDate).getTime(); if (isNaN(endDate)) { return; } setInterval(calculate, 1000); function calculate() { let startDate = new Date(); startDate = startDate.getTime(); let timeRemaining = parseInt((endDate - startDate) / 1000); if (timeRemaining &gt;= 0) { days = parseInt(timeRemaining / 86400); timeRemaining = (timeRemaining % 86400); hours = parseInt(timeRemaining / 3600); timeRemaining = (timeRemaining % 3600); minutes = parseInt(timeRemaining / 60); timeRemaining = (timeRemaining % 60); seconds = parseInt(timeRemaining); document.getElementById(&quot;days&quot;).innerHTML = parseInt(days, 10); document.getElementById(&quot;hours&quot;).innerHTML = (&quot;0&quot; + hours).slice(-2); document.getElementById(&quot;minutes&quot;).innerHTML = (&quot;0&quot; + minutes).slice(-2); document.getElementById(&quot;seconds&quot;).innerHTML = (&quot;0&quot; + seconds).slice(-2); } else { return; } } } (function() { countdown('08/05/2019 05:00:00 PM'); }()); &lt;/script&gt;  simpel tæller &lt;p class=&quot;counter&quot; data-count=some-number&gt;0&lt;/p&gt;  &lt;script type=&quot;text/javascript&quot;&gt; jQuery(document).ready(function($) { $('.counter').each(function() { var $this = $(this), // 400 can be changed or removed if just value shopwn or different count internval countTo = 400 - $this.attr('data-count'); $({ countNum: $this.text()}).animate({ countNum: countTo }, { duration: 8000, easing:'linear', step: function() { $this.text(Math.floor(this.countNum)); }, complete: function() { $this.text(this.countNum); //alert('finished'); } }); }); }); &lt;/script&gt;  Tooltip enable &lt;script&gt; jQuery(document).ready(function($) { $('[data-toggle=&quot;tooltip&quot;]').tooltip() }) &lt;/script&gt;  tablesorter-sort Add class
&lt;script type=&quot;text/javascript&quot;&gt; jQuery(document).ready(function($) { $(&quot;.views-table&quot;).addClass(&quot;tablesorter&quot;); }); &lt;/script&gt;  
Sort &lt;script type=&quot;text/javascript&quot;&gt; jQuery(document).ready(function($) { $(&quot;.views-table&quot;).tablesorter( {sortList: [[1,1]]} ); }); &lt;/script&gt;  Openers closers Premi Opener &lt;script type=&quot;text/javascript&quot;&gt; jQuery(document).ready(function($) { if ($.cookie('abo')) { $('.premi').attr('style', 'display: block !important'); } }); &lt;/script&gt;  Abon opener &lt;script type=&quot;text/javascript&quot;&gt; jQuery(document).ready(function($) { if ($.cookie('abo')) { $('.abon').attr('style', 'display: block !important'); } }); &lt;/script&gt;  Oui Oui source &lt;script type=&quot;text/javascript&quot;&gt; (function(root, factory) { if (typeof define === 'function' &amp;&amp; define.amd) { define(factory); } else if (typeof exports === 'object') { module.exports = factory(require,exports,module); } else { root.ouibounce = factory(); } }(this, function(require,exports,module) { return function ouibounce(el, config) { var config = config || {}, aggressive = config.aggressive || false, sensitivity = setDefault(config.sensitivity, 20), timer = setDefault(config.timer, 1000), delay = setDefault(config.delay, 0), callback = config.callback || function() {}, cookieExpire = setDefaultCookieExpire(config.cookieExpire) || '', cookieDomain = config.cookieDomain ? ';domain=' + config.cookieDomain : '', cookieName = config.cookieName ? config.cookieName : 'viewedOuibounceModal', sitewide = config.sitewide === true ? ';path=/' : '', _delayTimer = null, _html = document.documentElement; function setDefault(_property, _default) { return typeof _property === 'undefined' ? _default : _property; } function setDefaultCookieExpire(days) { // transform days to milliseconds var ms = days*24*60*60*1000; var date = new Date(); date.setTime(date.getTime() + ms); return &quot;; expires=&quot; + date.toUTCString(); } setTimeout(attachOuiBounce, timer); function attachOuiBounce() { _html.addEventListener('mouseleave', handleMouseleave); _html.addEventListener('mouseenter', handleMouseenter); _html.addEventListener('keydown', handleKeydown); } function handleMouseleave(e) { if (e.clientY &gt; sensitivity || (checkCookieValue(cookieName, 'true') &amp;&amp; !aggressive)) return; _delayTimer = setTimeout(_fireAndCallback, delay); } function handleMouseenter(e) { if (_delayTimer) { clearTimeout(_delayTimer); _delayTimer = null; } } var disableKeydown = false; function handleKeydown(e) { if (disableKeydown || checkCookieValue(cookieName, 'true') &amp;&amp; !aggressive) return; else if(!e.metaKey || e.keyCode !== 76) return; disableKeydown = true; _delayTimer = setTimeout(_fireAndCallback, delay); } function checkCookieValue(cookieName, value) { return parseCookies()[cookieName] === value; } function parseCookies() { // cookies are separated by '; ' var cookies = document.cookie.split('; '); var ret = {}; for (var i = cookies.length - 1; i &gt;= 0; i--) { var el = cookies[i].split('='); ret[el[0]] = el[1]; } return ret; } function _fireAndCallback() { fire(); callback(); } function fire() { // You can use ouibounce without passing an element // https://github.com/carlsednaoui/ouibounce/issues/30 if (el) el.style.display = 'block'; disable(); } function disable(options) { var options = options || {}; // you can pass a specific cookie expiration when using the OuiBounce API // ex: _ouiBounce.disable({ cookieExpire: 5 }); if (typeof options.cookieExpire !== 'undefined') { cookieExpire = setDefaultCookieExpire(options.cookieExpire); } // you can pass use sitewide cookies too // ex: _ouiBounce.disable({ cookieExpire: 5, sitewide: true }); if (options.sitewide === true) { sitewide = ';path=/'; } // you can pass a domain string when the cookie should be read subdomain-wise // ex: _ouiBounce.disable({ cookieDomain: '.example.com' }); if (typeof options.cookieDomain !== 'undefined') { cookieDomain = ';domain=' + options.cookieDomain; } if (typeof options.cookieName !== 'undefined') { cookieName = options.cookieName; } document.cookie = cookieName + '=true' + cookieExpire + cookieDomain + sitewide; // remove listeners _html.removeEventListener('mouseleave', handleMouseleave); _html.removeEventListener('mouseenter', handleMouseenter); _html.removeEventListener('keydown', handleKeydown); } return { fire: fire, disable: disable }; } ; })); &lt;/script&gt;  Oui call &lt;script&gt; jQuery(document).ready(function($) { if (!$.cookie('abo')) { // if you want to use the 'fire' or 'disable' fn, // you need to save OuiBounce to an object var _ouibounce = ouibounce(document.getElementById('ouibounce-modal'), { aggressive: false, timer: 2000, callback: function() { console.log('ouibounce fired!'); } }); $('body').on('click', function() { $('#ouibounce-modal').hide(); }); $('#ouibounce-modal .modal-footer').on('click', function() { $('#ouibounce-modal').hide(); }); $('#ouibounce-modal .modal-ouibounce').on('click', function(e) { e.stopPropagation(); }); } }); &lt;/script&gt;  Oui modal &lt;div id=&quot;ouibounce-modal&quot;&gt; &lt;div class=&quot;underlay&quot;&gt;&lt;/div&gt; &lt;div class=&quot;modal-ouibounce&quot;&gt; &lt;div class=&quot;modal-title&quot;&gt; &lt;h4&gt;Tak fordi De læste Folkets Avis&lt;/h4&gt; &lt;/div&gt; &lt;div class=&quot;modal-body&quot;&gt; &lt;div class=&quot;row&quot;&gt; &lt;div class=&quot;col-sm-8&quot;&gt; &lt;p&gt;Vi anbefaler et abonnement på avisen, så De&lt;/p&gt; &lt;ul&gt; &lt;li&gt;slipper for reklamer og popups som denne&lt;/li&gt; &lt;li&gt;får adgang til alle artikler, også journalistiske&lt;/li&gt; &lt;li&gt;får komplet fritekstsøgning i hele arkivet&lt;/li&gt; &lt;li&gt;får mulighed for daglige notifikationer&lt;/li&gt; &lt;li&gt;får mulighed for ugentligt nyhedsbrev&lt;/li&gt; &lt;li&gt;er med til at sikre mediets overlevelse&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;Det koster kun 2 kroner om dagen.&lt;/p&gt; &lt;/div&gt; &lt;div class=&quot;col-sm-4 alert-info alert&quot;&gt; &lt;p&gt;Danmarks uafhængige medie&lt;/p&gt; &lt;p class=&quot;small&quot;&gt;Vi får ikke mediestøtte, og De finder hos os en grundlæggende kritik af systemet, som mangler i andre aviser.&lt;/p&gt; &lt;/div&gt; &lt;/div&gt; &lt;div class=&quot;row&quot;&gt; &lt;div class=&quot;col-sm-12&quot;&gt; &lt;p class=&quot;text-center&quot; style=&quot;margin-top: 2em;&quot;&gt;&lt;a class=&quot;btn btn-danger btn-primary btn-lg&quot; href=&quot;/abonnement&quot; role=&quot;button&quot;&gt;Ja, tak – opret mig som abonnent!&lt;/a&gt;&lt;/p&gt; &lt;/div&gt; &lt;/div&gt; &lt;/div&gt; &lt;/div&gt; &lt;/div&gt;
